import React, { useState } from 'react';

function AddTaskForm() {

    const [taskBlock, setTaskBlock] = useState(false);
    const openTaskBlock = () => {
        setTaskBlock(true)
    }

    return (
        <div className="tasks__form">
            <div className='tasks__form-new'>
                {/* <img src="/" alt="Добавить" /> */}
                <span onClick={openTaskBlock}>Новая задача</span>
            </div>
            {
                !!taskBlock && <div className="tasks__form-block">
                    <input
                        className="field"
                        type="text"
                        placeholder="Текст задачи"
                    />
                    <button className="button">
                        Добавить задачу
                    </button>
                    <button className="button button--grey">Отмена</button>
                </div>
            }
        </div>
    );
}

export default AddTaskForm;
